Script.script:
function irc:apply_rules(sender, target, message, target_type)  
  local rules
  if target_type == "server" then
    rules = {
      exclusions = {},
      inclusions = {
        {patt = "^%[ERROR%]", repl = "<red>%0", targets = {"main", "misc"}},
        {patt = "^[!*] (.+)$", repl = "%1", targets = {"main", "misc"}},
        {patt = "^.*$", repl = f"%0", targets = {"main", "misc"}}
      }
    }
  elseif target_type == "nick" then
    rules = {
      exclusions = {},
      inclusions = {
        {patt = "^Your nick has changed.$", repl = f"Your nick has changed to {target}.", targets = {"main", "misc"}},
        {patt = "^[!*] (.+)$", repl = "<red>%1 (irc)", targets = {"main", "tells"}},
        {patt = "^.*$", repl = f"<red>{sender} tells you '%0' (irc)", targets = {"main", "tells"}}
      }
    }
  elseif target_type == "channel" then
    if sender == "wotdiscord" then
      rules = {
        exclusions = {},
        inclusions = {
          {patt = "^[!*] wotdiscord ([^:]+): (.+)$", repl = "%1 %2 (discord)", targets = {"main", "chats", "narrates"}},
          {patt = "^([^:]+): (.+)$", repl = "%1 chats '%2' (discord)", targets = {"main", "chats", "narrates"}},
          {patt = "^.*$", repl = f"{sender} chats '%0' (discord)", targets = {"main", "chats", "narrates"}}
        }
      }
    else
      rules = {
        exclusions = {},
        inclusions = {
          {patt = "^[!*] (.+)$", repl = "%1 (irc)", targets = {"main", "chats", "narrates"}},
          {patt = "^.*$", repl = f"{sender} chats '%0' (irc)", targets = {"main", "chats", "narrates"}}
        }
      }
    end
  end

  if self.gag then  
    table.insert(rules.exclusions, {patt = "^%*%*%*"})
    table.insert(rules.exclusions, {patt = "^%[INFO%]"})
    table.insert(rules.exclusions, {patt = "^%[MOTD%]"})
    for _, inclusion in ipairs(rules.inclusions) do
      inclusion.targets = table.n_complement(inclusion.targets, {"main"})
    end
  end

  local exclude = nil
  if rules.exclusions then
    for _, exclusion in ipairs(rules.exclusions) do
      if message:match(exclusion.patt) then
        exclude = exclusion
        break
      end
    end
  end

  local include = nil
  if rules.inclusions and not exclude then
    for _, inclusion in ipairs(rules.inclusions) do
      if message:match(inclusion.patt) then
        include = inclusion
        break
      end
    end
  end

  if include then
    message = message:gsub(include.patt, include.repl)
    message = message:gsub(getIrcConnectedHost() or getIrcServer(), "Server")
    return {
      message = message,
      targets = include.targets
    }
  end

  return nil
end

function irc:echo(message, targets) targets = targets or {"main", "misc"} local all_tabs = {"all", "narrates", "chats", "tells", "says", "yells", "misc"}
local wins = table.n_complement(targets, all_tabs) for _, win in ipairs(wins) do cecho(win, f"
<orange>{message}
") end
local tabs = table.n_intersection(targets, all_tabs) if not table.is_empty(tabs) then local time = getTime(true, "HH:mm") for _, tab in ipairs(table.n_union(tabs, {"all"})) do cecho(f"communications.{tab:title()}", f"{time} <orange>{message}
") end end end
